package quickquiz.kony.com.quiz;

import android.content.Context;
import android.content.pm.ActivityInfo;
import android.graphics.Bitmap;
import android.graphics.BitmapFactory;
import android.os.Bundle;
import android.os.PersistableBundle;
import android.support.annotation.Nullable;
import android.support.v7.app.AppCompatActivity;
import android.util.Base64;
import android.view.LayoutInflater;
import android.view.View;
import android.widget.ImageView;
import android.widget.LinearLayout;

import pl.polidea.view.ZoomView;

/**
 * Created by KIT912 on 2/6/2018.
 */

public class QuestionActivity extends AppCompatActivity {
    private ImageView imgques;
    private ZoomView zoomView;
    private LinearLayout main_container;
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.questionlayout);

        setRequestedOrientation(ActivityInfo.SCREEN_ORIENTATION_LANDSCAPE);
        String base64String = "data:image/png;base64,";
        String base64Image = base64String.split(",")[1];
        byte[] decodedString = Base64.decode(base64Image, Base64.DEFAULT);
        Bitmap decodedByte = BitmapFactory.decodeByteArray(decodedString, 0, decodedString.length);

        View v = ((LayoutInflater) getSystemService(Context.LAYOUT_INFLATER_SERVICE)).inflate(R.layout.questionview, null, false);
        v.setLayoutParams(new LinearLayout.LayoutParams(LinearLayout.LayoutParams.FILL_PARENT, LinearLayout.LayoutParams.FILL_PARENT));

        zoomView = new ZoomView(this);
        zoomView.addView(v);
        imgques= (ImageView) zoomView.findViewById(R.id.imgques);
        imgques.setImageBitmap(decodedByte);
        main_container = (LinearLayout) findViewById(R.id.main_container);
        main_container.addView(zoomView);
    }
}
